@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 0 0% 98%;
  --foreground: 220 15% 20%;
  --border: 220 8% 88%;

  --card: 0 0% 100%;
  --card-foreground: 220 15% 20%;
  --card-border: 220 8% 92%;

  --sidebar: 0 0% 96%;
  --sidebar-foreground: 220 15% 20%;
  --sidebar-border: 220 8% 86%;
  --sidebar-primary: 210 100% 45%;
  --sidebar-primary-foreground: 0 0% 100%;
  --sidebar-accent: 210 15% 88%;
  --sidebar-accent-foreground: 220 15% 20%;
  --sidebar-ring: 210 100% 45%;

  --popover: 0 0% 100%;
  --popover-foreground: 220 15% 20%;
  --popover-border: 220 8% 90%;

  --primary: 210 100% 45%;
  --primary-foreground: 0 0% 100%;

  --secondary: 220 8% 90%;
  --secondary-foreground: 220 15% 20%;

  --muted: 220 10% 93%;
  --muted-foreground: 220 10% 45%;

  --accent: 210 12% 92%;
  --accent-foreground: 220 15% 20%;

  --destructive: 0 85% 50%;
  --destructive-foreground: 0 0% 100%;

  --input: 220 12% 70%;
  --ring: 210 100% 45%;

  --chart-1: 210 100% 45%;
  --chart-2: 45 95% 55%;
  --chart-3: 142 70% 45%;
  --chart-4: 180 60% 45%;
  --chart-5: 30 90% 55%;

  --status-done: 142 70% 45%;
  --status-done-foreground: 0 0% 100%;
  --status-pending: 45 95% 50%;
  --status-pending-foreground: 220 15% 20%;
  --status-onhold: 30 90% 55%;
  --status-onhold-foreground: 0 0% 100%;
  --status-critical: 0 85% 50%;
  --status-critical-foreground: 0 0% 100%;
  --status-missed: 0 70% 35%;
  --status-missed-foreground: 0 0% 100%;
  --status-arrived: 180 60% 45%;
  --status-arrived-foreground: 0 0% 100%;
  --status-discharged: 220 10% 55%;
  --status-discharged-foreground: 0 0% 100%;

  --font-sans: Inter, system-ui, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .5rem;
  --shadow-2xs: 0px 1px 2px 0px hsl(220 15% 20% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(220 15% 20% / 0.08);
  --shadow-sm: 0px 2px 4px -1px hsl(220 15% 20% / 0.06), 0px 1px 2px -1px hsl(220 15% 20% / 0.05);
  --shadow: 0px 4px 6px -1px hsl(220 15% 20% / 0.08), 0px 2px 4px -1px hsl(220 15% 20% / 0.05);
  --shadow-md: 0px 6px 12px -2px hsl(220 15% 20% / 0.10), 0px 3px 6px -2px hsl(220 15% 20% / 0.05);
  --shadow-lg: 0px 10px 20px -4px hsl(220 15% 20% / 0.12), 0px 4px 8px -2px hsl(220 15% 20% / 0.06);
  --shadow-xl: 0px 20px 25px -5px hsl(220 15% 20% / 0.15), 0px 8px 10px -5px hsl(220 15% 20% / 0.08);
  --shadow-2xl: 0px 25px 50px -12px hsl(220 15% 20% / 0.20);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 220 15% 12%;
  --foreground: 0 0% 95%;
  --border: 220 10% 22%;

  --card: 220 12% 16%;
  --card-foreground: 0 0% 95%;
  --card-border: 220 10% 20%;

  --sidebar: 220 15% 14%;
  --sidebar-foreground: 0 0% 95%;
  --sidebar-border: 220 10% 18%;
  --sidebar-primary: 210 85% 55%;
  --sidebar-primary-foreground: 0 0% 100%;
  --sidebar-accent: 210 15% 20%;
  --sidebar-accent-foreground: 0 0% 95%;
  --sidebar-ring: 210 85% 55%;

  --popover: 220 12% 18%;
  --popover-foreground: 0 0% 95%;
  --popover-border: 220 10% 24%;

  --primary: 210 85% 55%;
  --primary-foreground: 0 0% 100%;

  --secondary: 220 8% 22%;
